lid || register_form.date.$invalid)) || ((register_form.year.$dirty &amp;&amp; register_form.year.$invalid) || (register_form.month.$dirty &amp;&amp; register_form.month.$invalid) || (register_form.date.$dirty &amp;&amp; register_form.date.$invalid) )"><small ng-show="register_form.year.$error.required || register_form.month.$error.required || register_form.date.$error.required">Please select your birth fields.</small></div></div><div class="cityInputWrapper relative"><input class="block" type="text" placeholder="City" name="city" ng-model="register.state" ng-minlength="3" ng-maxlength="50" ng-blur="" required=""><div class="formError" ng-show="register_form.city.$dirty &amp;&amp; register_form.city.$invalid &amp;&amp; register_form.city.$blurred"><small ng-show="register_form.city.$error.required">Enter your city here.</small> <small ng-show="register_form.city.$error.minlength || register_form.name.$error.maxlength">Please enter a valid city name.</small></div><div class="formError" ng-show="register_form.city.$pristine &amp;&amp; register_form.city.$invalid &amp;&amp; register_form.submitted"><small ng-show="register_form.city.$error.required">Enter your city here.</small></div></div><select class="block" name="country" ng-model="register.country" ng-options="val.name for val in countries" required=""></select><div class="genderInputWrapper relative"><div class="radioBtnContainer"><label class="inlineBlock">Male<input class="inlineBlock" name="gender" ng-model="register.gender" value="male" type="radio" required=""></label><label class="inlineBlock">Female<input class="inlineBlock" name="gender" ng-model="register.gender" value="female" type="radio" required=""></label></div><div class="formError" ng-show="(register_form.gender.$dirty &amp;&amp; register_form.gender.$invalid) || (register_form.gender.$pristine &amp;&amp; register_form.gender.$invalid &amp;&amp; register_form.submitted)"><small ng-show="register_form.gender.$error.required">Please select your gender.</small></div></div><button xhr-spinner="" class="btn btn-blue getStartedBtn"><span>I'm all set</span></button></form></div></div></div></div></div><footer><div class="copyrightInfo">Copyright © 2012-2013 Social Scientists e-commerce Pvt. Ltd. All Rights Reserved.</div></footer><script src="/application/views/dist/scripts/analyticsTracking.js"></script><script src="//ajax.googleapis.com/ajax/libs/angularjs/1.0.7/angular.min.js"></script><script src="//ajax.googleapis.com/ajax/libs/angularjs/1.0.7/angular-cookies.min.js"></script><script src="/application/views/dist/scripts/d2d85a55.landing.js"></script><script type="text/javascript">// Sticky elements on scroll
        var stickyElem = function(msgBanner, bodyElem, offset, bodyElemProp) {

//        var msgBanner = document.getElementById('msgBanner');
//        var bodyElem = document.querySelector('.fanciedProductsContainer');

//        var stop = (msgBanner.offsetTop + 57);
            var stop = (msgBanner.offsetTop + offset);
            console.log('offset is ' + offset + ' and stop value is ' + stop);

            window.onscroll = function(e) {
                var scrollTop = (window.pageYOffset !== undefined) ? window.pageYOffset : (document.documentElement || document.body.parentNode || document.body).scrollTop;
//        console.log(scrollTop, navBreadcrumbs.offsetTop);

                if (scrollTop >= stop) {
                    msgBanner.className = 'stick';
                    bodyElem.style.marginTop = bodyElemProp + 'px';
                } else {
                    msgBanner.className = '';
                    bodyElem.style.marginTop = '';
                }
            }
        };
        // End sticky elements on scroll</script></body></html>